import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { ethers } from 'ethers';

const projects = [
  {
    id: 1,
    title: 'Test 1',
    description: 'This is donation test 1.',
    targetAmount: 10, // 목표 금액
    deadline: '2025-12-31', // 마감 날짜
    contractAddress: '0xYourContractAddress1', // 스마트 컨트랙트 주소
  },
  {
    id: 2,
    title: 'Test 2',
    description: 'This is donation test 2.',
    targetAmount: 5,
    deadline: '2025-11-30',
    contractAddress: '0xYourContractAddress2',
  },
];

function ProjectDetail() {
  const { id } = useParams();
  const project = projects.find((p) => p.id === parseInt(id));
  const [amount, setAmount] = useState('');
  const [fundedAmount, setFundedAmount] = useState(0); // 현재 모인 금액

  const handleFund = async () => {
    if (!window.ethereum) {
      alert('Please install MetaMask!');
      return;
    }

    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const contract = new ethers.Contract(
        project.contractAddress,
        [
          'function donateEther() external payable', // 스마트 컨트랙트 함수
        ],
        signer
      );

      // 후원 트랜잭션 전송
      const txResponse = await contract.donateEther({ value: ethers.utils.parseEther(amount) });
      console.log('Transaction sent:', txResponse);

      // 트랜잭션 대기
      const txReceipt = await txResponse.wait();
      console.log('Transaction receipt:', txReceipt);

      // 후원 금액 업데이트
      setFundedAmount(fundedAmount + parseFloat(amount));

      alert(`후원 성공! 트랜잭션 해시: ${txResponse.hash}`);
    } catch (error) {
      console.error('Error sending transaction:', error);
      alert('후원 실패. 다시 시도해 주세요.');
    }
  };

  if (!project) {
    return <p>프로젝트를 찾을 수 없습니다.</p>;
  }

  return (
    <div style={{ padding: '2rem' }}>
      <h2>📌 프로젝트 상세: {project.title}</h2>
      <p>{project.description}</p>
      <p>🗓️ 마감 날짜: {project.deadline}</p>
      <p>🎯 목표 금액: {project.targetAmount} ETH</p>
      <p>💰 현재 모인 금액: {fundedAmount} ETH</p>
      <input
        type="number"
        placeholder="후원 금액 (ETH)"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
      />
      <button onClick={handleFund} style={{ marginLeft: '1rem' }}>
        💸 후원하기
      </button>
    </div>
  );
}

export default ProjectDetail;
