import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { ethers } from 'ethers';

const projects = [
  {
    id: 1,
    title: 'Test 1',
    description: 'This is donation test 1.',
    targetAmount: 10, // Î™©Ìëú Í∏àÏï°
    deadline: '2025-12-31', // ÎßàÍ∞ê ÎÇ†Ïßú
    contractAddress: '0xYourContractAddress1', // Ïä§ÎßàÌä∏ Ïª®Ìä∏ÎûôÌä∏ Ï£ºÏÜå
  },
  {
    id: 2,
    title: 'Test 2',
    description: 'This is donation test 2.',
    targetAmount: 5,
    deadline: '2025-11-30',
    contractAddress: '0xYourContractAddress2',
  },
];

function ProjectDetail() {
  const { id } = useParams();
  const project = projects.find((p) => p.id === parseInt(id));
  const [amount, setAmount] = useState('');
  const [fundedAmount, setFundedAmount] = useState(0); // ÌòÑÏû¨ Î™®Ïù∏ Í∏àÏï°

  const handleFund = async () => {
    if (!window.ethereum) {
      alert('Please install MetaMask!');
      return;
    }

    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const contract = new ethers.Contract(
        project.contractAddress,
        [
          'function donateEther() external payable', // Ïä§ÎßàÌä∏ Ïª®Ìä∏ÎûôÌä∏ Ìï®Ïàò
        ],
        signer
      );

      // ÌõÑÏõê Ìä∏ÎûúÏû≠ÏÖò Ï†ÑÏÜ°
      const txResponse = await contract.donateEther({ value: ethers.utils.parseEther(amount) });
      console.log('Transaction sent:', txResponse);

      // Ìä∏ÎûúÏû≠ÏÖò ÎåÄÍ∏∞
      const txReceipt = await txResponse.wait();
      console.log('Transaction receipt:', txReceipt);

      // ÌõÑÏõê Í∏àÏï° ÏóÖÎç∞Ïù¥Ìä∏
      setFundedAmount(fundedAmount + parseFloat(amount));

      alert(`ÌõÑÏõê ÏÑ±Í≥µ! Ìä∏ÎûúÏû≠ÏÖò Ìï¥Ïãú: ${txResponse.hash}`);
    } catch (error) {
      console.error('Error sending transaction:', error);
      alert('ÌõÑÏõê Ïã§Ìå®. Îã§Ïãú ÏãúÎèÑÌï¥ Ï£ºÏÑ∏Ïöî.');
    }
  };

  if (!project) {
    return <p>ÌîÑÎ°úÏ†ùÌä∏Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.</p>;
  }

  return (
    <div style={{ padding: '2rem' }}>
      <h2>üìå ÌîÑÎ°úÏ†ùÌä∏ ÏÉÅÏÑ∏: {project.title}</h2>
      <p>{project.description}</p>
      <p>üóìÔ∏è ÎßàÍ∞ê ÎÇ†Ïßú: {project.deadline}</p>
      <p>üéØ Î™©Ìëú Í∏àÏï°: {project.targetAmount} ETH</p>
      <p>üí∞ ÌòÑÏû¨ Î™®Ïù∏ Í∏àÏï°: {fundedAmount} ETH</p>
      <input
        type="number"
        placeholder="ÌõÑÏõê Í∏àÏï° (ETH)"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
      />
      <button onClick={handleFund} style={{ marginLeft: '1rem' }}>
        üí∏ ÌõÑÏõêÌïòÍ∏∞
      </button>
    </div>
  );
}

export default ProjectDetail;
