import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { useNavigate } from 'react-router-dom';

const projects = [
  {
    id: 1,
    title: 'Test 1',
    description: 'This is donation test 1.',
    targetAmount: 10, // Î™©Ìëú Í∏àÏï°
    deadline: '2025-12-31', // ÎßàÍ∞ê ÎÇ†Ïßú
    contractAddress: '0xYourContractAddress1', // Ïä§ÎßàÌä∏ Ïª®Ìä∏ÎûôÌä∏ Ï£ºÏÜå
  },
  {
    id: 2,
    title: 'Test 2',
    description: 'This is donation test 2.',
    targetAmount: 5,
    deadline: '2025-11-30',
    contractAddress: '0xYourContractAddress2',
  },
];

function Main() {
  const [walletAddress, setWalletAddress] = useState('');
  const navigate = useNavigate();

  const connectWallet = async () => {
    if (!window.ethereum) {
      alert('MetamaskÍ∞Ä ÏÑ§ÏπòÎêòÏñ¥ ÏûàÏßÄ ÏïäÏäµÎãàÎã§.');
      return;
    }

    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      await provider.send('eth_requestAccounts', []);
      const signer = provider.getSigner();
      const address = await signer.getAddress();
      setWalletAddress(address);
    } catch (err) {
      console.error('ÏßÄÍ∞ë Ïó∞Í≤∞ Ïã§Ìå®:', err);
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2 style={{ textAlign: 'center' }}>üî• ÏßÄÏõê Í∞ÄÎä•Ìïú ÌîÑÎ°úÏ†ùÌä∏</h2>

      {/* ÏßÄÍ∞ë Ïó∞Í≤∞ Î≤ÑÌäº */}
      <button onClick={connectWallet}>
        {walletAddress ? `üîó ${walletAddress.slice(0, 5)}...${walletAddress.slice(-4)}` : 'üîó Connect Wallet'}
      </button>

      {/* ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù */}
      <div style={{ display: 'flex', gap: '2rem', justifyContent: 'center', marginTop: '2rem' }}>
        {projects.map((project) => (
          <div
            key={project.id}
            style={{
              border: '1px solid #ccc',
              borderRadius: '1rem',
              padding: '1rem',
              width: '250px',
              cursor: 'pointer',
            }}
            onClick={() => navigate(`/project/${project.id}`)} // ÏÉÅÏÑ∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
          >
            <h3>{project.title}</h3>
            <p>{project.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Main;
