import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { useNavigate } from 'react-router-dom'; // ÎùºÏö∞ÌÑ∞ Ï∂îÍ∞Ä

function Main() {
  const [walletAddress, setWalletAddress] = useState('');
  const navigate = useNavigate(); // ÌéòÏù¥ÏßÄ Ïù¥Îèô Ìï®Ïàò

  const connectWallet = async () => {
    if (!window.ethereum) {
      alert('MetamaskÍ∞Ä ÏÑ§ÏπòÎêòÏñ¥ ÏûàÏßÄ ÏïäÏäµÎãàÎã§.');
      return;
    }

    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      await provider.send('eth_requestAccounts', []);
      const signer = provider.getSigner();
      const address = await signer.getAddress();
      setWalletAddress(address);
    } catch (err) {
      console.error('ÏßÄÍ∞ë Ïó∞Í≤∞ Ïã§Ìå®:', err);
    }
  };

  // Metamask Í≥ÑÏ†ï Î≥ÄÍ≤Ω Í∞êÏßÄ
  useEffect(() => {
    if (window.ethereum) {
      window.ethereum.on('accountsChanged', (accounts) => {
        if (accounts.length > 0) {
          setWalletAddress(accounts[0]);
        } else {
          setWalletAddress('');
        }
      });
    }
  }, []);

  return (
    <div style={{ padding: '2rem', textAlign: 'center' }}>
      <h2>Welcome to D-Fund</h2>
      <button onClick={connectWallet}>üîó Connect Wallet</button>

      {walletAddress && (
        <>
          <p>
            ‚úÖ Ïó∞Í≤∞Îêú ÏßÄÍ∞ë: <br />
            <strong>{walletAddress}</strong>
          </p>
          {/* ÌîÑÎ°úÏ†ùÌä∏ Îì±Î°ù Î≤ÑÌäº Ï∂îÍ∞Ä */}
          <button onClick={() => navigate('/register')} style={{ marginTop: '1rem' }}>
            ‚ûï ÌîÑÎ°úÏ†ùÌä∏ Îì±Î°ù
          </button>
        </>
      )}
    </div>
  );
}

export default Main;
